<?php  class Live_model extends CI_Model {        function Live_model()      {          // Call the Model constructor          parent::__construct();      }        function getx()          {              //Query the data table for every record and row              $query = $this->db->get('data');                               return $query->result();                       }	function insertx($tx,$y)	{		$this->title = $tx;		$this->text = $y;		$this->db->insert('data',$this);			}		function insert_user($arr)	{		$this->first_name = $arr['first_name'];		$this->last_name = $arr['last_name'];		$this->password = $arr['password'];		$this->username = $arr['username'];		$this->email = $arr['email'];		$this->country_id = $arr['country_id'];		$this->channel_id = $arr['channel_id'];		$this->reg_date = $arr['reg_date'];		$this->sex = $arr['sex'];		$this->last_login_date = time();//$arr('last_login_date');		$this->visa = "";//$arr['visa'];		$this->db->insert('user',$this);	}	function update_user($id,$arr)	{		$this->first_name = $arr['first_name'];		$this->last_name = $arr['last_name'];		$this->password = $arr['password'];		$this->username = $arr['username'];		$this->email = $arr['email'];		$this->country_id = $arr['country_id'];		$this->channel_id = $arr['channel_id'];		$this->reg_date = $arr['reg_date'];		$this->sex = $arr['sex'];		$this->last_login_date =time();// $arr('last_login_date');		$this->visa = 123456789;//$arr('visa');		$this->db->update('user',$this,array('id' => $id));	}	function insert_channel($ar)	{				$this->name = $ar['name'];		$this->disc = $ar['disc'];		$this->creation_date = $ar['creation_date'];				//************************ get free type ref -----------//		$this->type_id = $this->live_model->get_channel_type_byname('free');		$this->db->insert('channel',$this);	}	function get_channel_type_byname($s)	{		$query = $this->db->get_where('channel_type',array('name' =>$s));		$d= $query->result();			return $d[0]->id;	}	function get_channel_id_byname($s)	{		$query = $this->db->get_where('channel',array('name' =>$s));		$i =$query->result();		return $i[0]->id;	}						  }  ?> 